import React, {useEffect, useState} from "react";
import {BehaviorSubject,Subject} from "rxjs";
import {Movie} from "../../interfaces";

interface CardProps {
    item: Movie
    sendMessage?: (item: Movie) => void
}
const subject$ = new BehaviorSubject({})

const withObservableStream = (
    observable: { subscribe: (arg0: (newState: any) => void) => any },
    triggers: { sendMessage: (item: Movie) => void },
    initialState: Readonly<{}>,
) => (Component: React.ComponentType<CardProps>) => {
    return (props:CardProps) => {
        const [state, setState] = useState(initialState)
        useEffect(() => {
            let subscription = observable.subscribe(newState =>
                setState({...newState}),
            );
            return subscription.unsubscribe();
        },[])

        return (
            <Component {...props} {...state} {...triggers} />
        );
    }
};

const Card = (props: CardProps) => {
    console.log(props)
    const {rate, title, image,id} = props.item;
    const [titleValue, setTitleValue] = useState(title && title);
    const [rateValue, setRateValue] = useState(rate && rate.toString());
    const [edit, setEdit] = useState(false);

    const saveMovieRequest = () => {
        let newMovie = {
            id: id,
            rate: Number(rateValue),
            title: titleValue,
            image: image
        }
        props.sendMessage && props.sendMessage(newMovie)
        setEdit(!edit)
    };

    useEffect(() => {
        if (props.item.rate !== undefined) {
            setTitleValue(title)
            setRateValue(rate.toString())
        }
    },[props.item])

    if (!props.item) {
        return <div>loading</div>
    }
    return <div style={{padding: 10, display: "flex", flexFlow: "column"}}>
        <div style={{display: "flex", justifyContent: "space-between"}}>
            <div>Rate: {rate}</div>
            <div>Title: {title}</div>
        </div>
        <img width={200} height={300} src={image} alt={title}/>
        {!edit && <button onClick={() => setEdit(!edit)}>Редактировать</button>}
        {edit && <>
            <div>Title:</div>
            <input type="text" value={titleValue} onChange={event => setTitleValue(event.target.value)}/>
            <div>Rate:</div>
            <input type="text" value={rateValue} onChange={event => setRateValue(event.target.value)}/>
            <button onClick={ saveMovieRequest}>Сохранить</button>
        </>}
    </div>;
};

export default withObservableStream(
    subject$,
    {
        sendMessage: (item:Movie) => {
            console.log(item)
            subject$.next(item);
        },
    },
    {
        item: {},
    },
)(Card);
