import React, {useEffect, useState} from "react";
import {Subject} from "rxjs";
import {Movie} from "../../interfaces";
import Card from "./Card";

interface CardWrapperProps {
    items: Movie[]
}

const subject = new Subject();

export const movieService = {
    sendMessage: (movies: Movie[]) => subject.next(movies),
    clearMessages: () => subject.next(),
    onMovie: () => subject.asObservable(),
};

const CardWrapper = (props: CardWrapperProps) => {

    const [movies, setMovies] = useState<Movie[]>([]);

    useEffect(() => {
        const subscription = movieService.onMovie().subscribe(
            (movies) => console.log(movies)
        );

        return subscription.unsubscribe;
    }, []);

    useEffect(() => {
        props.items && setMovies(props.items)
    },[JSON.stringify(props.items)]);

    function sendMessage(movie: Movie) {
        const temp = props.items.filter(item => item.id !== movie.id)
        temp.push(movie)
        movieService.sendMessage(temp);
    }


    console.log(movies);
    return <>
        {movies.map((item: Movie) => <Card item={item} setMovie={sendMessage}/>)}
    </>;
};

export default CardWrapper;
