import React, {useEffect, useState} from "react";
import {Subject} from "rxjs";
import {Movie} from "../../interfaces";
import Card from "./Card";

interface CardWrapperProps {
    items: Movie[]
}

const subject = new Subject();

export const movieService = {
    sendMessage: (movie: Movie) => subject.next(movie),
    clearMessages: () => subject.next(),
    onMovie: () => subject.asObservable(),
};

const CardWrapper = (props: CardWrapperProps) => {

    const [movies, setMovies] = useState<Movie[]>([]);

    useEffect(() => {
        const subscription = movieService.onMovie().subscribe((movie: any) => {
            if (movie) {
                console.log('test',movie)
                const newMovies = movies.filter(item => item.id !== movie.id).push(movie);
                console.log('movies',movies)
                console.log('newMovies',newMovies)
                // setMovies(newMovies);
            }
            else {
                setMovies(props.items);
            }
        });

        return subscription.unsubscribe;
    }, []);

    useEffect(() => {
        props.items && setMovies(props.items)
    },[JSON.stringify(props.items)]);

    function sendMessage(movie: Movie) {
        movieService.sendMessage(movie);
    }

    function clearMessages() {
        movieService.clearMessages();
    }

    console.log(movies);
    return <>
        {movies.map((item: Movie) => <Card item={item} setMovie={sendMessage}/>)}
    </>;
};

export default CardWrapper;
