import React, {useEffect, useState} from "react";
import {BehaviorSubject} from "rxjs";
import {Movie} from "../../interfaces";

interface CardProps {
    item: Movie
}



const Card = (props: CardProps) => {
    const subject$ = new BehaviorSubject(props.item);

    const messageService = {
        sendMessage: (item:Movie) => subject$.next(item),
        onMessage: () => subject$.asObservable()
    };

    const {rate, title, image,id} = subject$.value;

    const [titleValue, setTitleValue] = useState(title);
    const [rateValue, setRateValue] = useState(rate.toString());
    const [edit, setEdit] = useState(false);

    useEffect(() => {
        messageService.onMessage().subscribe();
        return subject$.subscribe().unsubscribe();
    }, []);

    useEffect(() => {
        console.log(subject$.value)
    },[JSON.stringify(subject$.value)])

    const saveMovieRequest = () => {
        let newMovie = {
            id: id,
            rate: Number(rateValue),
            title: titleValue,
            image: image
        }
        messageService.sendMessage(newMovie)
        setEdit(!edit)
    };

    return <div style={{padding: 10, display: "flex", flexFlow: "column"}}>
        <div style={{display: "flex", justifyContent: "space-between"}}>
            <div>Rate: {rate}</div>
            <div>Title: {title}</div>
        </div>
        <img width={200} height={300} src={image} alt={title}/>
        {!edit && <button onClick={() => setEdit(!edit)}>Редактировать</button>}
        {edit && <>
            <div>Title:</div>
            <input type="text" value={titleValue} onChange={event => setTitleValue(event.target.value)}/>
            <div>Rate:</div>
            <input type="text" value={rateValue} onChange={event => setRateValue(event.target.value)}/>
            <button onClick={saveMovieRequest}>Сохранить</button>
        </>}
    </div>;
};

export default Card;
