import React, {useEffect, useState} from "react";
import {Subject} from "rxjs";
import {Movie} from "../../interfaces";
import Card from "./Card";

interface CardWrapperProps {
    items: Movie[]
}

const subject = new Subject();

export const messageService = {
    editMovie: (item: Movie) => subject.next(item),
    onEditMovie: () => subject.asObservable(),
};

const CardWrapper = (props: CardWrapperProps) => {
    console.log(props)
    const [items, setItems] = useState<Movie[]>([]);

    useEffect(() => {
        setItems(props.items);

        const subscription = messageService.onEditMovie().subscribe();

        return subscription.unsubscribe;
    }, []);

    useEffect(() => {
        console.log('asdfasd',items)
    },[items])
    return <>
        {items && items.map((item:Movie) => <Card item={item} setItem={messageService.editMovie}/>)}
    </> ;
};

export default CardWrapper;
