import React, {useEffect, useState} from "react";


/**
 Компонент карточки
 */
const SimpleExample = () => {

    const [A,setA] = useState<string>('')
    const [B,setB] = useState<string>('')
    const [C,setC] = useState<string>('')

    const checkC = () => {
        if (A !== '' && B !== '') {
            const res = Number(A) + Number(B)
            setC(res.toString())
        } else {
            setC('')
        }
    }

    // useEffect(() => {
    //     checkC()
    // },[A,B])

    const changeValueA = (val:string) => {
        setA(val)
        checkC()
    }

    const changeValueB = (val:string) => {
        setB(val)
        checkC()
    }

    console.log('render SimpleExample')

    return (
        <div style={{display: "flex",justifyContent:"space-evenly"}}>
            <div style={{display: "flex",flexFlow:"column"}}>
                <label>A</label>
                {/*<input type="number" value={A} onChange={event => setA(event.target.value)}/>*/}
                <input type="number" value={A} onChange={event => changeValueA(event.target.value)}/>
            </div>
            <div style={{display: "flex",flexFlow:"column"}}>
                <label>B</label>
            {/*<input type="number" value={B} onChange={event => setB(event.target.value)}/>*/}
            <input type="number" value={B} onChange={event => changeValueB(event.target.value)}/>
            </div>
            <div style={{display: "flex",flexFlow:"column"}}>
                <label>C</label>
            <input type="number" value={C} onChange={event => setC(event.target.value)}/>
            </div>
        </div>
    );
};

export default SimpleExample;
